# Directorio donde se encuentran las imágenes JPEG y donde se guardarán las PNG
directorio_entrada = r'C:\Users\marti\OneDrive\Escriptori\psiv2\Yolov8\foto'
directorio_salida = r'C:\Users\marti\OneDrive\Escriptori\psiv2\Yolov8\fotos'

for imagen_jpeg in imagenes_jpeg:
    # Componer la ruta completa de entrada y salida
    ruta_entrada = os.path.join(directorio_entrada, imagen_jpeg)
    nombre_salida = imagen_jpeg.replace('.jpeg', '.png')
    ruta_salida = os.path.join(directorio_salida, nombre_salida)

    # Abrir la imagen JPEG y guardarla como PNG
    with Image.open(ruta_entrada) as imagen:
        imagen.save(ruta_salida, 'PNG')

    print(f'Imagen {imagen_jpeg} convertida a PNG.')

print('Conversiones completadas.')
